import 'package:flutter/material.dart';class SaveNotesPresentation extends StatefulWidget {  final Map<String, dynamic>? note;  const SaveNotesPresentation({    Key? key,    this.note  }) : super(key: key);  @override  State<SaveNotesPresentation> createState() => _SaveNotesPresentationState();}class _SaveNotesPresentationState extends State<SaveNotesPresentation> {  bool get isEditing => widget.note != null;  TextEditingController titleController = TextEditingController();  TextEditingController descriptionController = TextEditingController();  @override  void initState() {    if (isEditing) {      titleController.text = widget.note?['title'];      descriptionController.text = widget.note?['description'];    }    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(title: Text(isEditing ? 'Créer une note' : 'Modifier la note n°${widget.note?['id']}'),),      body: Padding(        padding: const EdgeInsets.all(12.0),        child: Column(          children: <Widget> [            TextField(              controller: titleController,              decoration: const InputDecoration(                hintText: 'Titre'              ),            ),            const SizedBox(height: 8,),            TextField(              controller: descriptionController,              decoration: const InputDecoration(                hintText: 'description'              ),            ),            const SizedBox(height: 8,),            SizedBox(              width: double.infinity,              height: 50,              child: ElevatedButton(                onPressed: () {                  Navigator.of(context).pop();                },                child: Text(widget.note == null ? 'Ajouter une note' : 'Effectuer la modification'),                style: ElevatedButton.styleFrom(primary: Theme.of(context).primaryColor)              )            )          ],        ),      ),    );  }}