import 'package:flutter/material.dart';class SaveNotesPresentation extends StatelessWidget {  final int? id;  bool get isEditing => id == null;  const SaveNotesPresentation({Key? key, this.id}) : super(key: key);  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(title: Text(isEditing ? 'Créer une note' : 'Modifier la note n°$id'),),      body: Padding(        padding: const EdgeInsets.all(12.0),        child: Column(          children: <Widget> [            const TextField(              decoration: InputDecoration(                hintText: 'Titre'              ),            ),            const SizedBox(height: 8,),            const TextField(              decoration: InputDecoration(                hintText: 'description'              ),            ),            const SizedBox(height: 8,),            SizedBox(              width: double.infinity,              height: 50,              child: ElevatedButton(                onPressed: () {                  Navigator.of(context).pop();                },                child: Text(id == null ? 'Ajouter une note' : 'Effectuer la modification'),                style: ElevatedButton.styleFrom(primary: Theme.of(context).primaryColor)              )            )          ],        ),      ),    );  }}